package vkApi;import com.vk.api.sdk.client.TransportClient;import com.vk.api.sdk.client.VkApiClient;import com.vk.api.sdk.client.actors.UserActor;import com.vk.api.sdk.exceptions.ApiException;import com.vk.api.sdk.exceptions.ClientException;import com.vk.api.sdk.httpclient.HttpTransportClient;import com.vk.api.sdk.objects.enums.GroupsSort;import BackStage.secret;public class VkRepository {    private final int APP_ID= secret.APP_ID;    private final String CODE= secret.CODE;    private final VkApiClient vk;    private final UserActor actor;    private Integer groupId=null;    public VkRepository() {        TransportClient transportClient=new HttpTransportClient();        vk=new VkApiClient(transportClient);        actor=new UserActor(APP_ID,CODE);    }    public int getGroup(String name) throws ClientException, ApiException {        var b=vk.groups().search(actor,name)                .sort(GroupsSort.COMMENTS)                .execute()                .getItems();        var a=b.get(0);        return a.getId();    }    public int  getsearchPeple(String name) throws ClientException, ApiException {        if(groupId==null)            groupId=getGroup("Уральский федеральный университет | УрФУ");        var users=vk.users().search(actor)                .q(name)                .groupId(groupId).execute().getItems();        if (!users.isEmpty()){            var user=users.get(0);            if(!user.getIsClosed()) {                return vk.friends().get(actor)                        .userId(user.getId())                        .execute().getCount();            }        }        return 0;    }}